import Vue from "vue";
import { MatcherResult, MatcherComponent, MatcherComponentOptions } from "../utils";
declare global {
    namespace jest {
        interface Matchers<R, T> {
            /**
             * Asserts that the component requires the prop
             * @param {string} prop - The prop's name
             * @param options - Mount Option of the component
             * @example
             * expect(AComponent).toRequireProp("type")
             */
            toRequireProp(prop: string, options?: MatcherComponentOptions<Vue>): R;
        }
    }
}
export default function toRequireProp<V extends Vue>(received: MatcherComponent<V>, propName: string, dynamicMountOptions?: MatcherComponentOptions<V>): MatcherResult;
